SELECT /* [CodeDao][getCodeListForSelectBox][공통코드 SELECT BOX ][KimSeonHo][2022-12-20] */ TSC.CODE_CD VALUE , NVL(TSD.DOMAIN_NM,TSC.CODE_NM) NAME FROM tb_sy_code TSC LEFT JOIN tb_sy_domain TSD ON TSC.CODE_CD = TSD.DOMAIN_ID AND TSC.BIZ_CD = TSD.BIZ_CD AND TSD.LANG_CD = #{langCd} WHERE 1=1 AND TSC.BIZ_CD = #{s_bizCd} AND CODE_GRP_CD = #{codeGrpCd} AND TSC.USE_YN = 'Y'
AND TSC.CODE_OTHER1 = #{iobGbn}
ORDER BY CODE_ORDER ASC

   SELECT /* [CodeDao][getCodeDescListForSelectBox][공통코드 SELECT BOX 명+desc][KimSeonHo][2022-12-20] */
          CODE_NM VALUE
        , CODE_DESC || ' (' || CODE_NM || ')'  AS NAME
    FROM tb_sy_code
   WHERE DEL_YN = 'N'
     AND USE_YN = 'Y'
     AND BIZ_CD           = #{s_bizCd}
     AND CODE_GRP_CD      = #{codeGrpCd}

     AND CODE_CD          = #{codeCd}

     AND CODE_OTHER1      = #{dcCd}
ORDER BY CODE_GRP_CD ASC, CODE_ORDER ASC
