mapper.select:
SELECT /*[CommonDao.xml][findMaxItemCd] [공통] 아이템코드 신규채번 */
max(ITEM_CD)
FROM tb_ms_item
WHERE BIZ_CD = #{bizCd}
AND CLIENT_CD = #{clientCd}

SELECT /*[CommonDao.xml][findMaxSupplierCd] [공통] 공급처코드 신규채번 */
max(SUPPLIER_CD)
FROM tb_ms_supplier
WHERE BIZ_CD = #{bizCd}
AND CLIENT_CD = #{clientCd}

SELECT /*[CommonDao.xml][findMaxStoreCd] [공통] 배송처코드 신규채번 */
max(STORE_CD)
FROM tb_ms_store
WHERE BIZ_CD = #{bizCd}
AND CLIENT_CD = #{clientCd}
